import React, { useState, useEffect } from 'react';
import './css/App.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  // Load Google CSE script dynamically
  useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://cse.google.com/cse.js?cx=a26bc224caf624533"; // Your Google CSE ID here
    script.async = true;
    document.body.appendChild(script);

    // Cleanup the script after the component unmounts
    return () => {
      document.body.removeChild(script);
    };
  }, []);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { text: input, sender: "user" };
    setMessages(prev => [...prev, userMessage]);

    const response = await fetch("http://localhost:5000/api/chat", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ message: input })
    });

    const data = await response.json();
    const botMessage = { text: data.reply, sender: "bot" };

    // If there are search results, include them in the bot's response
    if (data.searchResults && data.searchResults.length > 0) {
      const searchResultsMessage = data.searchResults.map((result, index) => (
        <div key={index}>
          <p><strong>{result.title}</strong></p>
          <p>{result.snippet}</p>
          <a href={result.link} target="_blank" rel="noopener noreferrer">Read more</a>
        </div>
      ));
      botMessage.text = searchResultsMessage;
    }

    setMessages(prev => [...prev, botMessage]);
    setInput("");
  };

  return (
    <div className="chat-container">
      <h2>Chatbot</h2>
      <div className="chat-box">
        {messages.map((msg, i) => (
          <div key={i} className={`message ${msg.sender}`}>
            {typeof msg.text === 'string' ? msg.text : msg.text} {/* Render results as HTML */}
          </div>
        ))}
      </div>
      <div className="input-area">
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type a message..."
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}

export default App;
