import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Send form data to the backend
    try {
      const response = await axios.post('http://localhost:5000/submit', { name, email });
      alert(response.data.message); // Show success message
    } catch (error) {
      alert('Error submitting form!');
    }
  };

  const downloadPDF = async () => {
    try {
      const response = await axios.get('http://localhost:5000/download-pdf', {
        responseType: 'blob', // This is important for downloading files
      });

      // Create a link element to trigger the download
      const blob = new Blob([response.data], { type: 'application/pdf' });
      const link = document.createElement('a');
      link.href = URL.createObjectURL(blob);
      link.download = 'formData.pdf';
      link.click();
    } catch (error) {
      alert('Error downloading PDF!');
    }
  };

  return (
    <div className="App">
      <h1>Simple Form</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name: </label>
          <input 
            type="text" 
            value={name} 
            onChange={(e) => setName(e.target.value)} 
            required 
          />
        </div>
        <div>
          <label>Email: </label>
          <input 
            type="email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
            required 
          />
        </div>
        <button type="submit">Submit</button>
      </form>

      <button onClick={downloadPDF}>Download PDF</button>
    </div>
  );
}

export default App;
