import { useState } from "react";
import axios from "axios";

const CreateResume = () => {
  const [resume, setResume] = useState({
    name: "",
    email: "",
    phone: "",
    education: [{ institution: "", degree: "", year: "" }],
    experience: [{ company: "", role: "", duration: "" }],
    skills: [],
  });

  const handleChange = (e) => {
    setResume({ ...resume, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post("http://localhost:5000/api/resumes", resume);
    alert("Resume Created!");
  };

  return (
    <div className="p-8 max-w-lg mx-auto">
      <h2 className="text-2xl font-bold">Create Resume</h2>
      <input name="name" placeholder="Name" className="border p-2 w-full my-2" onChange={handleChange} />
      <input name="email" placeholder="Email" className="border p-2 w-full my-2" onChange={handleChange} />
      <input name="phone" placeholder="Phone" className="border p-2 w-full my-2" onChange={handleChange} />
      <button onClick={handleSubmit} className="mt-4 px-6 py-2 bg-green-500 text-white rounded">
        Save Resume
      </button>
    </div>
  );
};

export default CreateResume;
